//base64转Blob
const toBlob = function (urlData, fileType) {
  let bytes = window.atob(urlData),
    n = bytes.length,
    u8arr = new Uint8Array(n)
  while (n--) {
    u8arr[n] = bytes.charCodeAt(n)
  }
  return new Blob([u8arr], {type: fileType})
}

