      yield put({
        type: 'changeLoading',
        payload: false,
      })
    },

    // 源数据
    *exif({payload, callback}, {call, put}) {

      let url = payload.url + '?x-oss-process=image/info';

      yield FetchGet(url)
      .then((exif) => {
        if (exif.FNumber) {
          let FNumber = exif.FNumber.value                              //转换光圈值
          exif.FNumber.value = (FNumber.split('/')[0] / FNumber.split('/')[1]).toString()
        }
        return callback(exif)
      })
      .catch(function (err) {
        notification.error({
          message: 'exif查询失败！',
          description: err,
        })
      })
    },

    // 上传
